import React from "react";
import './App.css';
import Weather from './app_component/weather.component';
import 'bootstrap/dist/css/bootstrap.min.css';
import './weather-icons/css/weather-icons.css';



const API_KEY = "c54d675afed54f542494d45469f12850";
const proxyUrl = 'https://cors-anywhere.herokuapp.com/';
const targetUrl  = 'http://samples.openweathermap.org/data/2.5/weather?q=London,uk&appid=${API_KEY}';
class App extends React.Component{
  constructor(){
    super();
    this.state={
      city: undefined,
      country: undefined,
      icon: undefined,
      main: undefined,
      celsius: undefined,
      temp_max: undefined,
      temp_min: undefined,
      description : "",
      error : false
    };
    this.getWeather();
  }

  getWeather = async()=>{
    const api_call = await fetch(proxyUrl+targetUrl);

    const response = await api_call.json();
    console.log(response);

    this.setState({
      city:response.name,
      country:response.sys.country
    })
  }


  state = {};
  render(){
    return(
      <div className="App">
        <Weather city={this.state.city} country={this.state.country} />
      </div>
    );
  }
}

export default App;
